%--- 1: ConsumptionGG( 1, 6) = -1;                               % C(t)HH( 1, 6) = (h/(1+h));                        % C(t-1)FF( 1, 6) = (1/(1+h));                        % C(t+1)GG( 1, 7) = -((1-h)/((1+h)*sigma_c));         % R(t)FF( 1, 1) = ((1-h)/((1+h)*sigma_c));          % pi(t+1)MM( 1, 3) = ((1-h)/((1+h)*sigma_c));          % e_b(t)LL( 1, 3) = -((1-h)/((1+h)*sigma_c));         % e_b(t+1)%--- 2:  InvestmentGG( 2, 5) = -1;                               % I(t)HH( 2, 5) = (1/(1+betta));                    % I(t-1)FF( 2, 5) = (betta/(1+betta));                % I(t+1)GG( 2, 4) = (adj/(1+betta));                  % Q(t)LL( 2, 2) = (1+betta)*betta;                  % e_I(t+1)MM( 2, 2) = -(1+betta);                       % e_I(t)%--- 3:  Q-equationGG( 3, 4) = -1;                               % Q(t)GG( 3, 7) = -1;                               % R(t)FF( 3, 1) = 1;                                % pi(t+1)FF( 3, 4) = ((1-tau)/(1-tau+r_k));            % Q(t+1)JJ( 3, 1) = (r_k/(1-tau+r_k));                % r(t+1)MM( 3, 8) = 1;                                % n_Q(t)%--- 4:  CapitalGG( 4, 3) = -1;                               % K(t)HH( 4, 3) = (1-tau);                          % K(t-1)HH( 4, 5) = tau;                              % I(t-1)%--- 5:  PricesGG( 5, 1) = -1;                               % pi(t)FF( 5, 1) = (betta/(1+betta*gamma_p));        % pi(t+1)HH( 5, 1) = (gamma_p/(1+betta*gamma_p));      % pi(t-1)KK( 5, 1) = (((1-betta*xi_p)*(1-xi_p))/((1+betta*gamma_p)*xi_p))*alphaa;  % r(t)GG( 5, 2) = (((1-betta*xi_p)*(1-xi_p))/((1+betta*gamma_p)*xi_p))*(1-alphaa);  % w(t)MM( 5, 1) = -(((1-betta*xi_p)*(1-xi_p))/((1+betta*gamma_p)*xi_p));  % e_a(t)MM( 5, 7) = (((1-betta*xi_p)*(1-xi_p))/((1+betta*gamma_p)*xi_p));  % n_p(t)%--- 6:  WagesGG( 6, 2) = (-1-((1/(1+betta))*((1-betta*xi_w)*(1-xi_w))/((1+(1/lambda_w)*((1+lambda_w)*sigma_L))*xi_w)));  % w(t)FF( 6, 2) = (betta/(1+betta));                % w(t+1)HH( 6, 2) = (1/(1+betta));                    % w(t-1)FF( 6, 1) = (betta/(1+betta));                % pi(t+1)GG( 6, 1) = -((1+betta*gamma_w)/(1+betta));   % pi(t)HH( 6, 1) = (gamma_w/(1+betta));              % pi(t-1)KK( 6, 2) = ((1/(1+betta))*((1-betta*xi_w)*(1-xi_w))/((1+(1/lambda_w)*((1+lambda_w)*sigma_L))*xi_w))*sigma_L;  % L(t)HH( 6, 6) = -((1/(1+betta))*((1-betta*xi_w)*(1-xi_w))/((1+(1/lambda_w)*((1+lambda_w)*sigma_L))*xi_w))*(sigma_c/(1-h))*h;  % C(t-1)GG( 6, 6) = ((1/(1+betta))*((1-betta*xi_w)*(1-xi_w))/((1+(1/lambda_w)*((1+lambda_w)*sigma_L))*xi_w))*(sigma_c/(1-h));  % C(t)MM( 6, 4) = ((1/(1+betta))*((1-betta*xi_w)*(1-xi_w))/((1+(1/lambda_w)*((1+lambda_w)*sigma_L))*xi_w));  % e_L(t)MM( 6,10) = ((1/(1+betta))*((1-betta*xi_w)*(1-xi_w))/((1+(1/lambda_w)*((1+lambda_w)*sigma_L))*xi_w));  % n_w(t)%--- 7:  Labor demandCC( 1, 2) = -1;                               % L(t)AA( 1, 2) = -1;                               % w(t)CC( 1, 1) = (1+psi);                          % r(t)BB( 1, 3) = 1;                                % K(t-1)%--- 8:  ProductionAA( 2, 8) = -1;                               % Y(t)DD( 2, 1) = phi;                              % e_a(t)BB( 2, 3) = phi*alphaa;                       % K(t-1)CC( 2, 1) = phi*alphaa*psi;                   % r(t)CC( 2, 2) = phi*(1-alphaa);                   % L(t)%--- 9:     Goods marketGG( 7, 8) = -1;                               % Y(t)GG( 7, 6) = (1-tau*k-g);                      % C(t)GG( 7, 5) = tau*k;                            % I(t)MM( 7, 5) = 1;                                % e_G(t)%--- 10: Monetary policyGG( 8, 7) = -1;                               % R(t)HH( 8, 7) = rho;                              % R(t-1)HH( 8, 1) = ((1-rho)*r_pi-r_dpi);             % pi(t-1)MM( 8, 6) = (1-rho)*(1-r_pi);                 % e_pi(t)GG( 8, 8) = ((1-rho)*r_Y+r_dY);               % Y(t)GG( 8,16) = -((1-rho)*r_Y+r_dY);              % Y_f(t)GG( 8, 1) = r_dpi;                            % pi(t)HH( 8, 8) = -r_dY;                            % Y(t-1)HH( 8,16) = -r_dY;                            % Y_f(t-1)MM( 8, 9) = 1;                                % n_R(t)%--- 11:  Flexible ConsumptionGG( 9,14) = -1;                               % C_f(t)HH( 9,14) = (h/(1+h));                        % C_f(t-1)FF( 9,14) = (1/(1+h));                        % C_f(t+1)GG( 9,15) = -((1-h)/((1+h)*sigma_c));         % R_f(t)FF( 9, 9) = ((1-h)/((1+h)*sigma_c));          % pi_f(t+1)MM( 9, 3) = ((1-h)/((1+h)*sigma_c));          % e_b(t)LL( 9, 3) = -((1-h)/((1+h)*sigma_c));         % e_b(t+1)%--- 12:  Flexible InvestmentGG(10,13) = -1;                               % I_f(t)HH(10,13) = (1/(1+betta));                    % I_f(t-1)FF(10,13) = (betta/(1+betta));                % I_f(t+1)GG(10,12) = (adj/(1+betta));                  % Q_f(t)LL(10, 2) = (1+betta)*betta;                  % e_I(t+1)MM(10, 2) = -(1+betta);                       % e_I(t)%--- 13:  Flexible Q-equationGG(11,12) = -1;                               % Q_f(t)GG(11,15) = -1;                               % R_f(t)FF(11, 9) = 1;                                % pi_f(t+1)FF(11,12) = ((1-tau)/(1-tau+r_k));            % Q_f(t+1)JJ(11, 3) = (r_k/(1-tau+r_k));                % r_f(t+1)%--- 14:  Flexible CapitalGG(12,11) = -1;                               % K_f(t)HH(12,11) = (1-tau);                          % K_f(t-1)HH(12,13) = tau;                              % I_f(t-1)%--- 15:  Flexible PricesKK(13, 3) = alphaa;                           % r_f(t)GG(13,10) = (1-alphaa);                       % w_f(t)MM(13, 1) = -1;                               % e_a(t)%--- 16:  Flexible WagesGG(14,10) = -1;                               % w_f(t)KK(14, 4) = sigma_L;                          % L_f(t)HH(14,14) = -(sigma_c/(1-h))*h;               % C_f(t-1)GG(14,14) = (sigma_c/(1-h));                  % C_f(t)MM(14, 4) = -1;                               % e_L(t)%--- 17:  Flexible Labor demandCC( 3, 4) = -1;                               % L_f(t)AA( 3,10) = -1;                               % w_f(t)CC( 3, 3) = (1+psi);                          % r_f(t)BB( 3,11) = 1;                                % K_f(t-1)%--- 18:  Flexible ProductionAA( 4,16) = -1;                               % Y_f(t)DD( 4, 1) = phi;                              % e_a(t)BB( 4,11) = phi*alphaa;                       % K_f(t-1)CC( 4, 3) = phi*alphaa*psi;                   % r_f(t)CC( 4, 4) = phi*(1-alphaa);                   % L_f(t)%--- 19:  Flexible Goods marketGG(15,16) = -1;                               % Y_f(t)GG(15,14) = (1-tau*k-g);                      % C_f(t)GG(15,13) = tau*k;                            % I_f(t)MM(15, 5) = 1;                                % e_G(t)%--- 20:   Aggregate productivity shockNN( 1, 2) = rho_I;                            % e_I(t)%--- 21:   Adjustment cost shockNN( 2, 3) = rho_b;                            % e_b(t)%--- 22:   Preference shockNN( 3, 4) = rho_L;                            % e_L(t)%--- 23:   Labor supply shockNN( 4, 5) = rho_G;                            % e_G(t)%--- 24:   Public spending shockNN( 5, 1) = rho_a;                            % e_a(t)%--- 25:   Inflation targetNN( 6, 6) = rho_pi;                           % e_pi(t)%--- 26: Temporary first equationGG(16, 9) = -1;                               % pi_f(t)GG(16,17) = 0;                                % one(t)%--- 27: Temporary second equationGG(17,17) = -1;                               % one(t)HH(17,17) = 0;                                % one(t-1)%--- 26: Prices error expectationNN( 7, 7) = -1;                               % n_p(t)%--- 27: Tobin Q-ratio error expectationNN( 8, 8) = -1;                               % n_Q(t)%--- 28: Monetary policy interest rate error expectationNN( 9, 9) = -1;                               % n_R(t)%--- 29: Wages error expectationNN(10,10) = -1;                   